pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IDebondBond.sol";
import "./interfaces/IData.sol";

contract Exchange is AccessControl, ReentrancyGuard {

    using SafeERC20 for IERC20;
    using Counters for Counters.Counter;

    enum AuctionState {
        Started,
        Completed,
        Expired,
        Cancelled
    }

    struct Auction {
        uint id;
        address owner;
        uint256 startingTime;
        uint256 endingTime;
        uint256 duration;
        uint256 classId;
        uint256 nonceId;
        uint256 amount;
        uint256 maxDbitAmount;
        uint256 minDbitAmount;
        AuctionState auctionState;
        bool curvingPrice;
        address successfulBidder;
        uint256 finalPrice;
    }

    address bondData;
    address debondBondAddress;
    address currency;
    mapping(uint256 => Auction) public auctions;
    uint[] auctionsCollection;

    uint256 constant MAX_AUCTION_DURATION = 30 days;
    uint256 constant MIN_AUCTION_DURATION = 3600;

    Counters.Counter private idCounter;

    // events for the auctions

    event AuctionStarted(uint256 _auctionId, address issuer);
    event AuctionCancelled(uint256 _auctionId, address issuer, uint256 time);
    event AuctionCompleted(uint256 _auctionId, address BidWinner);
    event BidSubmitted(address indexed sender, uint256 amount);

    constructor(
        address _debondDataAddress,
        address _debondBondAddress,
        address _currency
    )  {
        bondData = _debondDataAddress;
        debondBondAddress = _debondBondAddress;
        currency = _currency;
        //   curvingPriceParam = 10;

    }

    modifier onlyAuctionOwner(uint256 _auctionId) {
        require(
            msg.sender == auctions[_auctionId].owner,
            "Exchange: Caller is not the auction owner"
        );
        _;
    }

    function createSecondaryMarketAuction(uint256 classId, uint256 nonceId, uint256 amount, uint256 minDbitAmount, uint256 maxDbitAmount, uint256 auctionDuration) external {

        // validation steps
        require(auctionDuration <= MAX_AUCTION_DURATION, "Exchange: Max Duration Exceeded");
        require(auctionDuration > MIN_AUCTION_DURATION, "Exchange: Min Duration not reached");
        require(minDbitAmount < maxDbitAmount, "Exchange: min Dbit Amount Should be less than max dbit amount");
        require(minDbitAmount > 0, "Exchange: min Dbit Amount Should be greater 0");

        IDebondBond(debondBondAddress).transferFrom(
            msg.sender,
            address(this),
            classId,
            nonceId,
            amount
        );

        (,IData.InterestRateType interestRateType,,) = IData(bondData).getClassFromId(classId);

        // adding the first object in the auction
        Auction storage auction = auctions[idCounter._value];
        auction.id = idCounter._value;
        auction.owner = msg.sender;
        auction.maxDbitAmount = maxDbitAmount;
        auction.duration = auctionDuration;
        auction.startingTime = block.timestamp;
        auction.classId = classId;
        auction.nonceId = nonceId;
        auction.amount = amount;
        auction.auctionState = AuctionState.Started;
        auction.curvingPrice = (interestRateType == (IData.InterestRateType.FloatingRate)) ? true : false ;


        auctionsCollection.push(auction.id);
        // increment the id
        idCounter.increment();

        emit AuctionStarted(auction.id, auction.owner);
    }

    function bid(uint256 _auctionId) nonReentrant() external {
        require(msg.sender != auctions[_auctionId].owner, "Exchange: bidder should not be the auction owner");
        checkIfExpired(_auctionId);
        Auction storage auction = auctions[_auctionId];
        require(
            auction.auctionState == AuctionState.Started,
            "bid is completed already"
        );
        address bidder = msg.sender;
        uint finalPrice = currentPrice(_auctionId);

        // 1.get his D/BIT first
        IERC20(currency).transferFrom(bidder, address(this), finalPrice);

        // 2.transfer him the bonds
        IDebondBond(debondBondAddress).transferFrom(address(this), bidder, auction.classId, auction.nonceId, auction.amount);

        // 3.change the state
        auction.successfulBidder = bidder;
        auction.endingTime = block.timestamp;
        auction.finalPrice = finalPrice;
        auction.auctionState = AuctionState.Completed;

        emit AuctionCompleted(_auctionId, bidder);
    }

    function cancelAuction(uint256 _auctionId) external onlyAuctionOwner(_auctionId) {
        checkIfExpired(_auctionId);
        Auction storage auction = auctions[_auctionId];
        require(auction.auctionState == AuctionState.Started, "auction already finished");

        IDebondBond(debondBondAddress).transferFrom(
            address(this),
            auction.owner,
            auction.classId,
            auction.nonceId,
            auction.amount
        );

        auction.auctionState = AuctionState.Cancelled;
        auction.endingTime = block.timestamp;
        emit AuctionCancelled(_auctionId, msg.sender, block.timestamp);
    }

    function getAuctionIds() external view returns(uint[] memory auctions) {
        return auctionsCollection;
    }

    function currentPrice(uint256 auctionId) public view returns (uint256 auctionPrice) {

        Auction memory auctionObject = auctions[auctionId];
        uint256 time_passed = block.timestamp - auctionObject.startingTime;
        require(
            time_passed < auctionObject.duration,
            "auction ended,equal to faceValue"
        );
        if (!auctionObject.curvingPrice) {
            // for fixed rate , there will be using the straight line fixed price decreasing mechanism.
            auctionPrice = auctionObject.maxDbitAmount - (auctionObject.maxDbitAmount - auctionObject.minDbitAmount) * time_passed / auctionObject.duration;

        }
        // else  if  its the floating rate, there will be decreasing parabolic curve as function of
        else {
            auctionPrice = auctionObject.maxDbitAmount - ((auctionObject.maxDbitAmount - auctionObject.minDbitAmount)/(auctionObject.duration**2)) * ((block.timestamp - auctionObject.startingTime)**2);
        }
    }

    function checkIfExpired(uint auctionId) private {
        Auction storage auction = auctions[auctionId];
        if(block.timestamp >= auction.startingTime + auction.duration) {
            auctions[auctionId].auctionState = AuctionState.Expired;
        }
    }

}
