import './APM.sol';
import './DebondData.sol';
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IAPM.sol";
import "./interfaces/IData.sol";
import "./interfaces/IDebondBond.sol";
import "./interfaces/IDebondToken.sol";
import "./libraries/CDP.sol";
import "./libraries/PRBMathSD59x18.sol";


contract Bank {

    using CDP for uint256;
    using SafeERC20 for IERC20;
    using PRBMathSD59x18 for uint256;


    IAPM apm;
    IData debondData;
    IDebondBond bond;
    enum PurchaseMethod {Buying, Staking}
    uint public constant BASE_TIMESTAMP = 1646089200; // 2022-03-01 00:00
    uint public constant DIFF_TIME_NEW_NONCE = 24 * 3600; // every 24h we crate a new nonce.
    uint public constant BENCHMARK_RATE_DECIMAL_18 = 5 * 10**16;
    address DBITAddress;

    constructor(
        address apmAddress,
        address dataAddress,
        address bondAddress,
        address _DBITAddress
    ) {
        apm = IAPM(apmAddress);
        debondData = IData(dataAddress);
        bond = IDebondBond(bondAddress);
        DBITAddress = _DBITAddress;
    }

    modifier ensure(uint deadline) {
        require(deadline >= block.timestamp, 'UniswapV2Router: EXPIRED');
        _;
    }

    // **** BUY BONDS ****

    function buyBond(
        uint _purchaseClassId, // token added
        uint _debondClassId, // token to mint
        uint _purchaseTokenAmount,
        uint _debondTokenMinAmount, //should be changed to interest min amount
        PurchaseMethod purchaseMethod
    ) external {

        uint purchaseClassId = _purchaseClassId;
        uint debondClassId = _debondClassId;
        uint purchaseTokenAmount = _purchaseTokenAmount;
        uint debondTokenMinAmount = _debondTokenMinAmount;

        require(debondData.canPurchase(debondClassId, purchaseClassId), "Pair not Allowed");


        (,,address purchaseTokenAddress,) = debondData.getClassFromId(purchaseClassId);
        (,IData.InterestRateType interestRateType ,address debondTokenAddress,) = debondData.getClassFromId(debondClassId);

        uint amountBToMint = calculateDebondTokenToMint(
//            purchaseTokenAddress,
//            debondTokenAddress,
            purchaseTokenAmount
        );

//        require(debondTokenMinAmount <= amountBToMint, "Not enough debond token in minting calculation");


        IERC20(purchaseTokenAddress).transferFrom(msg.sender, address(apm), purchaseTokenAmount);
        //see uniswap : transferhelper,ierc202
        IDebondToken(debondTokenAddress).mint(address(apm), 2*amountBToMint); // One time for pool with tokenA, one time got pool with dgov
        // be aware that tokenB is a DebondToken, maybe add it to the class model
        //amountDgov = cdp_DGOV(amountBToMint)                               TODO
        //IDebondToken(dGOVTokenAddress).mint(address(apm), amountDgov );    TODO

        apm.updateWhenAddLiquidity(purchaseTokenAmount, amountBToMint,  purchaseTokenAddress,  debondTokenAddress);
        //apm.updateWhenAddLiquidity(amountBToMint, amountDgov,  debondTokenAddress, dgovTokenAddress);   TODO

        (uint fixedRate, uint floatingRate) = interestRate(purchaseClassId, debondClassId, purchaseTokenAmount, purchaseMethod);
        if (purchaseMethod == PurchaseMethod.Staking) {
            issueBonds(msg.sender, purchaseClassId, purchaseTokenAmount);
            (uint reserveA, uint reserveB) = apm.getReserves(purchaseTokenAddress, debondTokenAddress);
            //if reserve == 0 : use cdp price instead of quote? See with yu
            //do we have to handle the case where reserve = 0? or when deploying, we put some liquidity?
            uint amount = CDP.quote(purchaseTokenAmount, reserveA, reserveB);
            uint rate = interestRateType == IData.InterestRateType.FixedRate ? fixedRate : floatingRate;
            issueBonds(msg.sender, debondClassId, amount * rate / 10**18);
        }
        else if (purchaseMethod == PurchaseMethod.Buying) {
            (uint reserveA, uint reserveB) = apm.getReserves(purchaseTokenAddress, debondTokenAddress);
            uint amount = CDP.quote(purchaseTokenAmount, reserveA, reserveB);
            uint rate = interestRateType == IData.InterestRateType.FixedRate ? fixedRate : floatingRate;
            issueBonds(msg.sender, debondClassId, amount + amount * rate / 10**18); // here the interest calculation is hardcoded. require the interest is enough high
        }
    }

    // **** REDEEM BONDS ****

    function redeemBonds(
        uint classId,
        uint nonceId,
        uint amount
        //uint amountMin?
    ) external {
        IDebondBond(address(bond)).redeem(msg.sender, classId, nonceId, amount);
	    //require(redeemable) is already done in redeem function for liquidity, but still has to be done for time redemption

        (, IData.InterestRateType interestRateType ,address TokenAddress,) = debondData.getClassFromId(classId);
        //require(reserves[TokenAddress]>amountIn);



        if(interestRateType == IData.InterestRateType.FixedRate) {
            (,,,,,uint maturityDate) = bond.bondDetails( classId, nonceId);
            require(block.timestamp>maturityDate);
            IERC20(TokenAddress).transferFrom(address(apm), msg.sender, amount);
            apm.updateTotalReserve(TokenAddress, amount);


        }
        else if (interestRateType == IData.InterestRateType.FloatingRate){
            //to be implemented later
        }

        //how do we know if we have to burn (or put in reserves) dbit or dbgt?


	    //APM.removeLiquidity(tokenAddress, amountIn);
//        apm.updaReserveAfterRemovingLiquidity(tokenAddress, amountIn);
        //emit

    }

    // **** Swaps ****

    function swap(address tokenIn, address tokenOut, uint amountIn, uint amountOutMin) public {
        //if (tokenIn == dbitAddress){

        //}

    }

    // **** SWAP ****
    // requires the initial amount to have already been sent to the first pair
    function _swap(uint[] memory amounts, address[] memory path, address to) internal virtual {
        for (uint i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            uint amountOut = amounts[i + 1];
            (uint amount0Out, uint amount1Out) = (uint(0), amountOut);
            apm.swap(
                amount0Out, amount1Out, input, output, to
            );
        }
    }
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path
    ) public returns (uint[] memory amounts) {
        amounts = apm.getAmountsOut(amountIn, path);
        require(amounts[amounts.length - 1] >= amountOutMin, 'UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT');

        IERC20(path[0]).transferFrom(msg.sender, address(apm), amounts[0]);
        _swap(amounts, path, msg.sender); //msg.sender?
    }




    // TODO External to the Bank maybe
    function calculateDebondTokenToMint(
//        address purchaseTokenAddress, // token added
//        address debondTokenAddress, //token minted
        uint purchaseTokenAmount
    ) internal pure returns (uint) {
        return purchaseTokenAmount;
    }


    function issueBonds(address to, uint256 classId, uint256 amount) private {
        uint timestampToCheck = block.timestamp;
        (uint lastNonceId, uint createdAt) = debondData.getLastNonceCreated(classId);
        createdAt = createdAt == 0 ? BASE_TIMESTAMP : createdAt;
        if ((timestampToCheck - createdAt) >= DIFF_TIME_NEW_NONCE) {
            createNewNonce(classId, lastNonceId, timestampToCheck);
            (uint nonceId,) = debondData.getLastNonceCreated(classId);
            bond.issue(to, classId, nonceId, amount);
            return;
        }

        uint tDay = (timestampToCheck - BASE_TIMESTAMP) % DIFF_TIME_NEW_NONCE;
        if ((tDay + (timestampToCheck - createdAt)) >= DIFF_TIME_NEW_NONCE) {
            createNewNonce(classId, lastNonceId, timestampToCheck);
            (uint nonceId,) = debondData.getLastNonceCreated(classId);
            bond.issue(to, classId, nonceId, amount);
            return;
        }
    }

    function createNewNonce(uint classId, uint lastNonceId, uint creationTimestamp) private {
        uint _newNonceId = ++lastNonceId;
        (,,, uint period) = debondData.getClassFromId(classId);
        bond.createNonce(classId, _newNonceId, creationTimestamp + period, 500);
        debondData.updateLastNonce(classId, _newNonceId, creationTimestamp);
        //here 500 is liquidity info hard coded for now
    }

    function interestRate(
        uint _purchaseTokenClassId,
        uint _debondTokenClassId,
        uint _purchaseTokenAmount,
        PurchaseMethod purchaseMethod
    ) public view returns (uint fixRate, uint floatRate) {
        uint purchaseTokenClassId = _purchaseTokenClassId;
        uint debondTokenClassId = _debondTokenClassId;
        uint purchaseTokenAmount = _purchaseTokenAmount;

        uint fixRateSupply = 0;
        uint floatRateSupply = 0;

        (,IData.InterestRateType interestRateType, address purchaseTokenAddress,) = debondData.getClassFromId(purchaseTokenClassId); // address of the purchase token


        // staking collateral for bonds
        if (purchaseMethod == PurchaseMethod.Staking) {
            fixRateSupply = bond.bondAmountDue(purchaseTokenAddress, IData.InterestRateType.FixedRate);// we get the fix rate bonds supply
            floatRateSupply = bond.bondAmountDue(purchaseTokenAddress, IData.InterestRateType.FloatingRate);// we get the float rate bonds supply

            // we had the client amount to the according bond balance to calculate interest rate after deposit
            if (purchaseTokenAmount > 0 && interestRateType == IData.InterestRateType.FixedRate) {
                fixRateSupply += purchaseTokenAmount;
            }
            if (purchaseTokenAmount > 0 && interestRateType == IData.InterestRateType.FloatingRate) {
                floatRateSupply += purchaseTokenAmount;
            }

        }
        // buying Bonds
        else if (purchaseMethod == PurchaseMethod.Buying) {

            (,,address debondTokenAddress,) = debondData.getClassFromId(debondTokenClassId); // address of D/BIT


            uint debondTokenAmount = getDebondTokenAmountFrom(purchaseTokenAddress, purchaseTokenAmount);

            fixRateSupply = bond.bondAmountDue(debondTokenAddress, IData.InterestRateType.FixedRate);
            floatRateSupply = bond.bondAmountDue(debondTokenAddress, IData.InterestRateType.FloatingRate);

            if (interestRateType == IData.InterestRateType.FixedRate) {
                fixRateSupply += debondTokenAmount;
            }
            if (interestRateType == IData.InterestRateType.FloatingRate) {
                floatRateSupply += debondTokenAmount;
            }
        }

        if (fixRateSupply == 0 || floatRateSupply == 0) {
            fixRate = 2 * BENCHMARK_RATE_DECIMAL_18 / 3;
            floatRate = 2 * fixRate;
        } else {
            int sigmoidCParam = PRBMathSD59x18.inv(3);
            uint x = uint(PRBMathSD59x18.div(int(fixRateSupply), int(floatRateSupply + fixRateSupply)));
            floatRate = (2 * BENCHMARK_RATE_DECIMAL_18 * uint(PRBMathSD59x18.sigmoid(int(x), sigmoidCParam)) / 1e18);
            fixRate = 2 * BENCHMARK_RATE_DECIMAL_18 - floatRate;
        }
    }

    function getDebondTokenAmountFrom(address tokenAddress, uint tokenAmount) public view returns (uint) {
        // TODO we need to call oracle to get USDAmount from tokenAddress and tokenAmount
        uint USDAmountDecimal8 = tokenAmount; // Here we simulate that 1 token = $1
        return USDToDbitToMint(USDAmountDecimal8);

    }

    function USDToDbitToMint(uint amountUSDDecimal8) public view returns (uint) {
        return amountUSDDecimal8;
    }

}
